package com.hb;import cn.hutool.core.util.CharsetUtil;import cn.hutool.crypto.symmetric.SymmetricCrypto;/** * @author hubing * @date 2021/09/14 17:51 * * MS4加密解密 **/public class MS3 {    public static void main(String[] args) {        String content = "test中文";        SymmetricCrypto sm4 = new SymmetricCrypto("SM4");        String encryptHex = sm4.encryptHex(content);        System.out.println(encryptHex);        String s = sm4.decryptStr(encryptHex, CharsetUtil.CHARSET_UTF_8);        System.out.println(s);    }}