package com.tools;/** * @author hubing * @date 2021/09/16 11:52 **/public class Utils {    public static void main(String[] args) {        SM2KeyPair sm2Keys = SM2Utils.getSm2Keys(false);        System.out.println("公钥: " + sm2Keys.getPublicKey());        System.out.println("私钥: " + sm2Keys.getPrivateKey());        String data = "123";        System.out.println(data);        String e = SM2Utils.encrypt(sm2Keys.getPublicKey(), data);        System.out.println("密文: " + e);        String decrypt = SM2Utils.decrypt(sm2Keys.getPrivateKey(), e);        System.out.println("解秘数据: " + decrypt);        System.out.println("明文密文是否相同 ：" + data.equals(decrypt));    }}